import React, { Component } from 'react';
import { Link, NavLink } from 'react-router-dom';
import styled from 'styled-components';
import { ButtonContainer } from './Button';
import { ThemeContext } from './context/ThemeContexts';
import { FaRegMoon } from 'react-icons/fa';
import { GoSun } from 'react-icons/go';
import { AiOutlineMenu } from 'react-icons/ai';
import { ProductConsumer } from '../context';
import './Navbar.css';

class Navbar extends Component {
  static contextType = ThemeContext;

  constructor(props) {
    super(props);
    this.state = {
      isMobile: window.innerWidth <= 768,
      menuOpen: false,
    };

    this.handleResize = this.handleResize.bind(this);
    this.handleMenu = this.handleMenu.bind(this);
  }

  componentDidMount() {
    window.addEventListener('resize', this.handleResize);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleResize);
  }

  handleResize() {
    this.setState({ isMobile: window.innerWidth <= 768 });
  }

  handleMenu() {
    this.setState((prevState) => ({ menuOpen: !prevState.menuOpen }));
  }

  render() {
    const { theme, toggleTheme } = this.context;
    const { isMobile, menuOpen } = this.state;

    return (
      <div>
        {isMobile ? (
          // Mobile View
          <MobileNavWrapper className="navbar">
            <Link to="/" className="navbar-brand">
              <img src={require('../logo.png')} alt="store" />
            </Link>
            <div className="menu-icon" onClick={this.handleMenu}>
              <AiOutlineMenu size={30} />
            </div>
            {menuOpen && (
              <div className="menu">
                <NavLink to="/" className={({ isActive }) => (isActive ? 'text-primary' : 'text-white')}>
                  Products
                </NavLink>
                <ProductConsumer>
                  {value => (
                    <input
                      placeholder="Search for products"
                      onChange={(e) => value.filterProducts(e.target.value)}
                    />
                  )}
                </ProductConsumer>
                <div className="themes" onClick={toggleTheme}>
                  {theme ? <FaRegMoon size={20} /> : <GoSun size={20} />}
                </div>
                <Link to="/cart">
                  <ButtonContainer>
                    <i className="fas fa-cart-plus"> my cart</i>
                  </ButtonContainer>
                </Link>
              </div>
            )}
          </MobileNavWrapper>
        ) : (
          // Desktop View
          <DesktopNavWrapper className="navbar">
            <Link to="/" className="navbar-brand">
              <img src={require('../logo.png')} alt="store" />
            </Link>
            <ul className="navbar-nav">
              <li className="nav-item">
                <NavLink to="/" className="nav-link">
                  Products
                </NavLink>
              </li>
              <ProductConsumer>
                {value => (
                  <li className="nav-item">
                    <input
                      placeholder="Search for products"
                      onChange={(e) => value.filterProducts(e.target.value)}
                    />
                  </li>
                )}
              </ProductConsumer>
              <li className="nav-item">
                <Link to="/cart">
                  <ButtonContainer>
                    <i className="fas fa-cart-plus"> my cart</i>
                  </ButtonContainer>
                </Link>
              </li>
            </ul>
            <div className="themes" onClick={toggleTheme}>
              {theme ? <FaRegMoon size={20} /> : <GoSun size={20} />}
            </div>
          </DesktopNavWrapper>
        )}
      </div>
    );
  }
}

const NavWrapper = styled.nav``;

const MobileNavWrapper = styled(NavWrapper)`
  .menu-icon {
    display: flex;
    justify-content: flex-end;
    align-items: center;
  }
  .menu {
    display: flex;
    flex-direction: column;
    align-items: center;
  }
`;

const DesktopNavWrapper = styled(NavWrapper)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  .navbar-nav {
    display: flex;
    align-items: center;
  }
`;

export default Navbar;
