import React, { Component } from 'react';
import { Link, NavLink } from 'react-router-dom';
import styled from 'styled-components';
import { ButtonContainer } from './Button';
import { ThemeContext } from './context/ThemeContexts';
import { FaRegMoon } from 'react-icons/fa';
import { GoSun } from 'react-icons/go';
import { AiOutlineMenu } from 'react-icons/ai';
import { ProductConsumer } from '../context';
import './Navbar.css';

class Navbar extends Component {
  static contextType = ThemeContext;

  constructor(props) {
    super(props);
    this.state = {
      isMobile: window.innerWidth <= 768,
      menuOpen: false,
    };

    this.handleResize = this.handleResize.bind(this);
    this.handleMenu = this.handleMenu.bind(this);
  }

  componentDidMount() {
    window.addEventListener('resize', this.handleResize);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleResize);
  }

  handleResize() {
    this.setState({
      isMobile: window.innerWidth <= 768,
    });
  }

  handleMenu() {
    this.setState((prevState) => ({ menuOpen: !prevState.menuOpen }));
  }

  render() {
    const { theme, toggleTheme } = this.context;
    const { isMobile, menuOpen } = this.state;

    return (
      <div>
        {isMobile ? (
          <MobileNavWrapper className="navbar navbar-expand-sm bg-slate-800 px-sm-5 w-100">
            <Link to="/" className="w-50">
              <img src={require('../logo.png')} alt="store" className="navbar-brand" />
            </Link>
            <div className="text-white w-50 menu" onClick={this.handleMenu}>
              <AiOutlineMenu className="menubar" />
            </div>
            {menuOpen && (
              <div className="resmenu w-100">
                <NavLink to="/" className={({ isActive }) => isActive ? "text-primary" : "text-white hover"}>
                  Products
                </NavLink>
                <ProductConsumer>
                  {value => (
                    <li style={{ listStyleType: 'none' }}>
                      <input 
                        placeholder='Search for products'
                        onChange={(e) => value.filterProducts(e.target.value)}
                      />
                    </li>
                  )}
                </ProductConsumer>
                <Link className="text-white bg-transparent themes" onClick={toggleTheme}>
                  {theme ? <h6>Dark Mode <FaRegMoon /></h6> : <h6>Light Mode <GoSun /></h6>}
                </Link>
                <Link to="/cart" className="ml-auto">
                  <ButtonContainer>
                    <i className="fas fa-cart-plus">my cart</i>
                  </ButtonContainer>
                </Link>
              </div>
            )}
          </MobileNavWrapper>
        ) : (
          <DesktopNavWrapper className="navbar navbar-expand-sm bg-slate-800 px-sm-5">
            <Link to="/">
              <img src={require('../logo.png')} alt="store" className="navbar-brand" />
            </Link>
            <ul className="navbar-nav align-items-center">
              <li className="nav-item ml-5">
                <NavLink to="/" className="nav-link">
                  Products
                </NavLink>
              </li>
            </ul>
            <ul className="navbar-nav align-items-center">
              <ProductConsumer>
                {value => (
                  <li className="nav-item ml-5">
                    <input 
                      placeholder='Search for products'
                      onChange={(e) => value.filterProducts(e.target.value)}
                    />
                  </li>
                )}
              </ProductConsumer>
            </ul>
            <Link to="/cart" className="ml-auto">
              <ButtonContainer>
                <i className="fas fa-cart-plus">my cart</i>
              </ButtonContainer>
            </Link>
            <div className="text-white bg-transparent themes mainmenu" onClick={toggleTheme}>
              {theme ? <FaRegMoon /> : <GoSun />}
            </div>
          </DesktopNavWrapper>
        )}
      </div>
    );
  }
}

const NavWrapper = styled.nav`
  .nav-link {
    color: var(--mainWhite) !important;
    font-size: 1.3rem;
    text-transform: capitalize;
  }
`;

const MobileNavWrapper = styled(NavWrapper)`
  /* Add mobile-specific styles here */
  .resmenu {
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: var(--mainDark);
    padding: 1rem;
  }
`;

const DesktopNavWrapper = styled(NavWrapper)`
  /* Add desktop-specific styles here */
  .navbar-nav {
    display: flex;
    align-items: center;
  }
`;

export default Navbar;
